export class PlaceService {
    constructor(http, _authService) {
        this.http = http;
        this._authService = _authService;
        this.urlApi = environment.url_api;
        this.local = this._authService.getInfoLocal();
    }
    handleError(err) {
        let errorMessage;
        if (err.error instanceof ErrorEvent) {
            errorMessage = `An error occurred: ${err.error.message}`;
        }
        else {
            errorMessage = `Server returned code: ${err.status}, error message is: ${err.message}`;
        }
        throw new Error(errorMessage);
    }
    addPlace(Place) {
        //Place.companyId = this.local.companyId;
        const URI = this.urlApi + 'places/create';
        return this.http.post(URI, Place).
            pipe(catchError(async (err) => {
            if (err.status == 401) {
                this._authService.logout();
            }
            this.handleError(err);
        }));
    }
    updatePlace(id, Place) {
        const URI = this.urlApi + `places/update/${id}`;
        return this.http.put(URI, Place).
            pipe(catchError(async (err) => {
            if (err.status == 401) {
                this._authService.logout();
            }
            this.handleError(err);
        }));
    }
    deletePlace(id) {
        const URI = this.urlApi + `places/delete/${id}`;
        return this.http.delete(URI).pipe(catchError(async (err) => {
            if (err.status == 401) {
                this._authService.logout();
            }
            this.handleError(err);
        }));
    }
    getPlaceById(id) {
        let companyId = this.local.companyId;
        const URI = this.urlApi + `places/findById/${id}`;
        let data = {
            "companyId": companyId
        };
        return this.http.post(URI, data).
            pipe(catchError(async (err) => {
            if (err.status == 401) {
                this._authService.logout();
            }
            this.handleError(err);
        }));
    }
    getPlacesByCompany() {
        const URI = this.urlApi + `places/listar`;
        let companyId = this.local.companyId;
        let data = {
            "companyId": companyId
        };
        return this.http.post(URI, data).
            pipe(catchError(async (err) => {
            if (err.status == 401) {
                this._authService.logout();
            }
            this.handleError(err);
        }));
    }
}
